import React from "react";
import { useFormik } from 'formik';
import  validator  from 'validator';
// TODO: import useFormik from formik library

function App() {
  // TODO: add a const called formik assigned to useFormik()
  const [error, setError] = React.useState('');
  const [show, setShow] = React.useState(false);


  const formik = useFormik({
    initialValues: {
      email: '',
      password: '',
    },
    onSubmit: values => {
      console.log(values);
    },
    validate: values => {
      console.log(JSON.stringify(values.validate));
     // const errors = {};
     // if (!email) errors.emailField = 'Field required';
    }
  });

  return (
    <div>
      <form onSubmit = {formik.handleSubmit}>
        <div>Email</div>
        <input name='email' type='email' onChange={ formik.handleChange} value={formik.values.email} />
        <div>password</div>
        <input name='password' type='text' onChange={ formik.handleChange} value={formik.values.password} />
        {show && <h5>{error }</h5>}  
        <div>
          <button type='submit'>Submit</button>
        </div>
      </form>
    </div>
  );
}

export default App;
