import React from "react";
import { useFormik } from 'formik';
import  validator  from 'validator';
// TODO: import useFormik from formik library

function App() {
  // TODO: add a const called formik assigned to useFormik()

  const formik = useFormik({
    initialValues: {
      email: '',
      password: '',
    },
    onSubmit: values => {
      console.log(values.email);

    },
    validate: values => {
      let errors = {};
      if (!values.emailField) errors.emailField = "Field required";
      if (!values.passwordField) errors.passwordField = "Field required";
      if (values.emailField && !validator.isEmail(values.emailField)) errors.emailField = "Username should be an email";
      //if (values.email && values.password && validator.isEmail(values.email)) alert = "Login Successfull";
      return errors;
    }
  });

  return (
    <div>
      <form onSubmit = {formik.handleSubmit}>
        <div>Email</div>
        <input name='email' type='email' onChange={ formik.handleChange} value={formik.values.email} />
        {formik.errors.emailField ? (<div>{ formik.errors.emailField}</div>) : null}
        <div>Password</div>
        <input name='password' type='text' onChange={ formik.handleChange} value={formik.values.password} />
        {formik.errors.passwordField ? (<div>{ formik.errors.passwordField}</div>) : null}  
        <div>
          <button type='submit'>Submit</button>
        </div>
      </form>
    </div>
  );
}

export default App;
